css:
.format-demo {
    max-width: 8.5in;
    margin: 0 auto;
    padding: 0.25in;
    font-size: 10pt;
}

.code-sample {
    text-align: center;
}

/* 1D Barcode styling */
.barcodes-1d .code-sample svg {
    max-width: 100%;
    height: 0.4in;
    margin: 0 auto;
}

/* QR code styling */
.qr-wrapper {
    width: 1.2in;
    height: 1.2in;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
}

.qr-wrapper svg {
    width: 100%;
    height: 100%;
    max-width: 1.2in;
    max-height: 1.2in;
}

@media print {
    .format-demo {
        padding: 0;
    }

    .gallery {
        page-break-inside: avoid;
    }

    .code-sample {
        page-break-inside: avoid;
    }
}

.shelf-label {
    width: 2.25in;
    height: 1.25in;
    padding: 0.125in;
    font-family: Arial, sans-serif;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    border: 1px solid #ccc;
}

.primary-info {
    margin-bottom: 0.0625in;
}

.item-name {
    font-size: 8pt;
    font-weight: bold;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.item-code {
    font-size: 7pt;
    color: #666;
}

.barcode {
    text-align: center;
    margin: 0.0625in 0;
}

.barcode svg {
    max-width: 2in;
    height: 0.375in;
}

.stock-info {
    display: flex;
    justify-content: space-between;
    font-size: 7pt;
    color: #444;
}

@media print {
    @page {
        size: 2.25in 1.25in;
        margin: 0;
    }

    .shelf-label {
        border: none;
    }
}
